import React from "react";
import Inputs from "./Inputs";
// import Panel from "./Panel";
import "../styles/App.css";

function App() {

  const [entry, updateEntry] = useState([]);
  let itemsList = entry;

  const [text, setText] = useState("");
  function changed(e) {
    setText(e.target.value);
  }

  function addEntry(e) {
    e.preventDefault();
    const item = { text };
    updateEntry((itemsList) => [...itemsList, item]);
    console.log(itemsList);
    setText("");
    console.log(uuid.v4());
  }

  function deleteEntry(e){
    const rem = e.target.getAttribute("id");
    let tempp = itemsList.filter((item, id)=>{
      if(id != rem){
        return item;
      }
    })
    updateEntry(tempp);
  }



  return (
    <div className="main">
      <div className="inputs">
        {/* <Inputs /> */}
        <form onSubmit={addEntry}>
          <input
            type="text"
            placeholder="Add items to your notes"
            onChange={changed}
            value={text}
          />
          <button>
            <AddCircleOutlineRoundedIcon />
          </button>
        </form>
        <ul>
          {itemsList.map((item, id) => {
            return (
              <li key={id} id={id} value={item.text} onClick={deleteEntry}>
                {item.text}
              </li>
            );
          })}
        </ul>




      </div>
      <div className="sider"></div>
    </div>
  );
}
export default App;
