import React, {useState} from "react";
import AddCircleOutlineRoundedIcon from '@mui/icons-material/AddCircleOutlineRounded';

let itemsList;

function Inputs(){
  const [entry, updateEntry] = useState([]);

  const [text, setText] = useState("");
  function changed(e){
    setText(e.target.value);
  }

  function addEntry(e){
    e.preventDefault();
    // const item = {text};
    const item = {
      task: {text},
    }
    console.log(item);
    updateEntry(itemsList=>[...itemsList, item]);
    console.log(itemsList);
    setText("");
    itemsList.map(i =>{
      return i.task;
    })
  }

  return (
    <>
      <form onSubmit={addEntry}>
        <input type="text" placeholder="Add items to your notes" onChange={changed} value={text} />
        <button><AddCircleOutlineRoundedIcon /></button>
      </form>
      {/* <div>
        {itemsList.map(entry =>{
          return {entry};
        })}
      </div> */}
    </>
  );
}
export default Inputs;
