import React, {useState} from "react";
import AddCircleOutlineRoundedIcon from '@mui/icons-material/AddCircleOutlineRounded';

function Inputs(){

  let itemsList = [];

  const [text, setText] = useState("");
  function changed(e){
    setText(e.target.value);
  }

  function addEntry(){
    const item = {text};
    console.log(item);
    // itemsList.push(item);
    itemsList = [...itemsList, item];
    console.log(itemsList);
  }

  return (
    <>
      <input type="text" placeholder="Add items to your notes" onChange={changed} />
      <button onClick={addEntry}><AddCircleOutlineRoundedIcon /></button>
    </>
  );
}
export default Inputs;
