import React, {useState} from "react";
import AddCircleOutlineRoundedIcon from "@mui/icons-material/AddCircleOutlineRounded";
import "../styles/App.css";

function App() {
  const [entry, updateEntry] = useState([]);
  let itemsList = entry;

  const [text, setText] = useState("");
  function changed(e) {
    setText(e.target.value);
  }

  function addEntry(e) {
    e.preventDefault();
    const item = {
      text: text,
      date: Date()
    };
    updateEntry((itemsList) => [...itemsList, item]);
    setText("");
    // console.log(uuid.v4());
  }


  const [head, setHead] = useState("notes topic");
  const [desc, setDesc] = useState("enter the desc")
  const [date, setDate] = useState("")
  function sider(e){
    const h = e.target.getAttribute("value");
    setHead(h);
    const i = e.target.getAttribute("id");
    updateCurr(i);
    let finder = itemsList.filter((item, id)=>{
      if(id==i){
        return item;
      }
    })
    setDate(finder[0].date);
  }
  function updateDesc(e){
    const d = e.target.value;
    setDesc(d);
    const now = Date();
    console.log(e);
    const i = e.target.getAttribute("id");
    let finder = itemsList.filter((item, id) =>{
      if(id == i) return item;
    })
    console.log(finder);
    console.log(e.target.id);
  }

  const [curr, updateCurr] = useState(-1);

  return (
    <div className="main">
      <div className="inputs">
        {/* <Inputs /> */}
        <form onSubmit={addEntry}>
          <input
            type="text"
            placeholder="Add items to your notes"
            onChange={changed}
            value={text}
          />
          <button>
            <AddCircleOutlineRoundedIcon />
          </button>
        </form>
        <ul>
          {itemsList.map((item, id) => {
            return (
              <li key={id} id={id} value={item.text} onClick={sider}>
                {item.text}
              </li>
            );
          })}
        </ul>
      </div>



      <div className="sider">
        <h1 className="head">{head}</h1>
        {/* <input className="desc" placeholder="enter the description" onChange={updateDesc} value={desc} /> */}
        <p>{date}</p>
        <input className="desc" id={curr} onChange={updateDesc} value={desc} />
      </div>

    </div>
  );
}
export default App;
