import React, { useState } from "react";
import AddCircleOutlineRoundedIcon from "@mui/icons-material/AddCircleOutlineRounded";

const uuid = require("uuid");

function Inputs() {
  const [entry, updateEntry] = useState([]);
  let itemsList = entry;

  const [text, setText] = useState("");
  function changed(e) {
    setText(e.target.value);
  }

  function addEntry(e) {
    e.preventDefault();
    // const item = {text};
    const item = { text };
    updateEntry((itemsList) => [...itemsList, item]);
    console.log(itemsList);
    setText("");
    console.log(uuid.v4());
  }

  // function deleteEntry(event) {
  //   const rem = event.target.getAttribute("value");
  //   itemsList.filter((item) => {
  //     console.log(item.text, rem)
  //     if(item.text != rem) return item
  //     else  console.log("shiic");
  //   });
  //   updateEntry(itemsList);
  //   // console.log(itemsList);
  // }
  function deleteEntry(e){
    const rem = e;
    itemsList.filter((item, id)=>{
      if(item.id != rem) return item;
    })
    updateEntry(itemsList);
  }

  return (
    <>
      <form onSubmit={addEntry}>
        <input
          type="text"
          placeholder="Add items to your notes"
          onChange={changed}
          value={text}
        />
        <button>
          <AddCircleOutlineRoundedIcon />
        </button>
      </form>
      <ul>
        {itemsList.map((item, id) => {
          return (
            <li key={id} value={item.text} onClick={deleteEntry}>
              {item.text}
            </li>
          );
        })}
      </ul>
    </>
  );
}
export default Inputs;
