import React from "react";
import Inputs from "./Inputs";
// import Panel from "./Panel";
import "../styles/App.css";

function App() {
  return (
    <div className="main">
      <div className="inputs">
        {/* <Inputs /> */}
        <form onSubmit={addEntry}>
          <input
            type="text"
            placeholder="Add items to your notes"
            onChange={changed}
            value={text}
          />
          <button>
            <AddCircleOutlineRoundedIcon />
          </button>
        </form>
        <ul>
          {itemsList.map((item, id) => {
            return (
              <li key={id} id={id} value={item.text} onClick={deleteEntry}>
                {item.text}
              </li>
            );
          })}
        </ul>




      </div>
      <div className="sider"></div>
    </div>
  );
}
export default App;
