  let itemsList = entry;

  const [text, setText] = useState("");
  function changed(e){
    setText(e.target.value);
  }

  function printItems(){
    itemsList.map(i =>{
      console.log(i);
      // console.log("shivi");
    })
  }

  function addEntry(){
    const item = {text};
    console.log(item);
    updateEntry([...itemsList, item]);
    console.log(itemsList);
    console.log(itemsList.length);
    setText("");

    printItems();
  }

  return (
    <form onS>
      <input type="text" placeholder="Add items to your notes" onChange={changed} value={text} />
      <button><AddCircleOutlineRoundedIcon /></button>
    </form>
  );
}
export default Inputs;
