import React, { useState } from "react";
import AddCircleOutlineRoundedIcon from "@mui/icons-material/AddCircleOutlineRounded";

const uuid = require("uuid");

function Inputs() {
  const [entry, updateEntry] = useState([]);
  let itemsList = entry;

  const [text, setText] = useState("");
  function changed(e) {
    setText(e.target.value);
  }

  function addEntry(e) {
    e.preventDefault();
    // const item = {text};
    const item = { text };
    updateEntry((itemsList) => [...itemsList, item]);
    console.log(itemsList);
    setText("");
    console.log(uuid.v4());
  }

  function deleteEntry(e){
    const rem = e.target.getAttribute("id");
    let tempp = itemsList.filter((item, id)=>{
      console.log(id, rem)
      if(id != rem){
        console.log(item)
        return item;
      }
    })
    console.log(tempp);
    updateEntry(itemsList);
    // console.log(itemsList)
  }

  return (
    <>
      <form onSubmit={addEntry}>
        <input
          type="text"
          placeholder="Add items to your notes"
          onChange={changed}
          value={text}
        />
        <button>
          <AddCircleOutlineRoundedIcon />
        </button>
      </form>
      <ul>
        {itemsList.map((item, id) => {
          return (
            <li key={id} id={id} value={item.text} onClick={deleteEntry}>
              {item.text}
            </li>
          );
        })}
      </ul>
    </>
  );
}
export default Inputs;
