import React, {useState} from "react";
import AddCircleOutlineRoundedIcon from "@mui/icons-material/AddCircleOutlineRounded";
import "../styles/App.css";

function App() {
  const [entry, updateEntry] = useState([]);
  let itemsList = entry;

  const [text, setText] = useState("");
  function changed(e) {
    setText(e.target.value);
  }

  function addEntry(e) {
    e.preventDefault();
    const item = { text };
    updateEntry((itemsList) => [...itemsList, item]);
    console.log(itemsList);
    setText("");
    // console.log(uuid.v4());
  }

  // function deleteEntry(e) {
  //   const rem = e.target.getAttribute("id");
  //   let tempp = itemsList.filter((item, id) => {
  //     if (id != rem) {
  //       return item;
  //     }
  //   });
  //   updateEntry(tempp);
  // }

  const [head, setHead] = useState("");
  const [desc, setDesc] = useState("")
  function sider(e){
    const h = e.target.getAttribute("value");
    setHead(h);
  }
  function updateDesc(e){
    const d = e.target.value;
    setDesc(d);
  }

  return (
    <div className="main">
      <div className="inputs">
        {/* <Inputs /> */}
        <form onSubmit={addEntry}>
          <input
            type="text"
            placeholder="Add items to your notes"
            onChange={changed}
            value={text}
          />
          <button>
            <AddCircleOutlineRoundedIcon />
          </button>
        </form>
        <ul>
          {itemsList.map((item, id) => {
            return (
              <li key={id} id={id} value={item.text} onClick={sider}>
                {item.text}
              </li>
            );
          })}
        </ul>
      </div>



      <div className="sider">
        <h1 className="head">{head}</h1>
        <input className="desc" placeholder="enter the description" onChange={updateDesc} />
      </div>

    </div>
  );
}
export default App;
